#include <QtGui>
#include "user_interface.h"



User_Interface::User_Interface(QWidget *parent) : QFrame(parent)
    {
        setFrameStyle(QFrame::Panel | QFrame::Sunken);
        setFocusPolicy(Qt::StrongFocus);
        GameStarted = false;
        GamePaused = false;
        AutoPlayMode = false;
        clearBoard();
        Random_Next_Piece();
    }



void User_Interface::NextBlockLabel(QLabel *label)
    {
        Next_Block_Label = label;
    }



QSize User_Interface::WindowSize() const
    {
        return QSize(Board_Width() * 15 + frameWidth() * 2,
                     Board_Height() * 15 + frameWidth() * 2);
    }

QSize User_Interface::MinimumWindowSize() const
    {
        return QSize(Board_Width() * 5 + frameWidth() * 2,
                     Board_Height() * 5 + frameWidth() * 2);
    }



void User_Interface::start()
    {
        if (GamePaused)
            return;

        GameStarted = true;
        WaitingAfterLine = false;
        AutoPlayMode = false;
        RemovedRows = 0;
        Points = 0;
        clearBoard();

        emit RowsRemoveChange(RemovedRows);
        emit PointsChange(Points);
        NewBlock();
        timer.start( 1000 , this);
    }



void User_Interface::pause()
    {
        GamePaused = !GamePaused;
        if (GamePaused)
            {
                timer.stop();
            }
        else
            {
                timer.start( 1000 , this);
            }
        update();
    }



void User_Interface::autoplay()
    {
        if (GamePaused)
            return;

        GameStarted = false;
        WaitingAfterLine = false;
        AutoPlayMode = true;
        RemovedRows = 0;
        Points = 0;
        clearBoard();

        emit RowsRemoveChange(RemovedRows);
        emit PointsChange(Points);
        NewBlock();
        timer.start( 3000 , this);
    }



void User_Interface::paintEvent(QPaintEvent *event)
    {
        QFrame::paintEvent(event);
        QPainter painter(this);
        QRect rect;
        rect.setCoords(0,0,Board_Width()*PixelWidth(),Board_Height()*PixelHeight());

        if (GamePaused)
            {
                painter.drawText(rect, Qt::AlignCenter, tr("Pause"));
                return;
            }


        int boardTop = rect.bottom() - Board_Height()*PixelHeight();

        for (int i = 0; i < Board_Height() ; ++i)
            {
                for (int j = 0; j <Board_Width() ; ++j)
                    {
                        BlockType_E shape = PixelLocation(j, i );
                        if (shape != No_Shape())
                        drawSquare(painter , rect.left() + j * PixelWidth() , boardTop + i * PixelHeight() , shape);
                    }

            }

        if (Current_Shape() != No_Shape())
            {
                for (int i = 0; i < 4; ++i)
                    {
                        int x = Current_Shape_Position_X( i );
                        int y = Current_Shape_Position_Y( i );
                        drawSquare( painter , rect.left() + x * PixelWidth() , y  * PixelHeight() , Current_Shape() );
                    }
            }
    }



void User_Interface::keyPressEvent(QKeyEvent *event)
    {
        if (!GameStarted || GamePaused || AutoPlayMode || Current_Shape() == No_Shape())
            {
                QFrame::keyPressEvent(event);
                return;
            }

        switch (event->key())
            {
                case Qt::Key_Left:
                    Move(MoveLeft_e);
                    update();
              break;
                case Qt::Key_Right:
                    Move(MoveRight_e);
                    update();
              break;
                case Qt::Key_Down:
                    Move(RotateCounterClockwise_e);
                    update();
              break;
                case Qt::Key_Up:
                    Move(RotateClockwise_e);
                    update();
              break;
                case Qt::Key_Space:
                    Move(Release_e);
                    BlockDropped();
              break;
                case Qt::Key_D:
                    VerticalMovement();
              break;
                default:
                   QFrame::keyPressEvent(event);
            }

    }



void User_Interface::ShowNextBlock()
    {
        if (!Next_Block_Label)
            return;

        int dx = Pixel_Length_X() + 1;
        int dy = Pixel_Length_Y() + 1;

        QPixmap pixmap(dx * PixelWidth(), dy * PixelHeight());
        QPainter painter( &pixmap );
        painter.fillRect( pixmap.rect(), Next_Block_Label->palette().background() );

        for (int i = 0; i < 4; ++i)
            {
                int x = Next_Shape_Position_X( i );
                int y = Next_Shape_Position_Y( i );
                drawSquare( painter , x * PixelWidth(), y * PixelHeight() , GetNextBlockType() );
            }
        Next_Block_Label->setPixmap(pixmap);

    }



void User_Interface::drawSquare(QPainter &painter, int x, int y, BlockType_E shape)
    {
        static const QRgb colorTable[7] =
            {
                0xCC6666, 0x66CC66, 0x6666CC,
                0xCCCC66, 0xCC66CC, 0x66CCCC, 0xDAAA00
            };

        QColor color = colorTable[int(shape)];
        painter.fillRect ( x + 1 , y + 1 , PixelWidth() - 2 , PixelHeight() - 2 , color );
        painter.setPen(color.light());
        painter.drawLine( x , y + PixelHeight() - 1 , x , y );
        painter.drawLine(x , y , x + PixelWidth() - 1 , y) ;
        painter.setPen(color.dark());
        painter.drawLine( x + 1 , y + PixelHeight() - 1 , x + PixelWidth() - 1 , y + PixelHeight() - 1 );
        painter.drawLine( x + PixelWidth() - 1 , y + PixelHeight() - 1 , x + PixelWidth() - 1 , y + 1 );
    }



void User_Interface::VerticalMovement()
    {
        if (!MoveTo(CurrentBlock, Current_X_cordinate, Currenty_Y_cordinate + 1))
        BlockDropped();
    }



void User_Interface::BlockDropped()
    {
        for (int i = 0; i < 4; ++i)
            {
                int x = Current_Shape_Position_X( i );
                int y = Current_Shape_Position_Y( i );
                PixelLocation(x, y) = Current_Shape();
            }

        RemoveRow();

        if(IsFinished())
            {
                CurrentBlock.ChooseShape(No_Shape());
                timer.stop();
                GameStarted = false;
                AutoPlayMode = false;
            }

        if (!WaitingAfterLine)
            NewBlock();
    }



void User_Interface::RemoveRow()
    {
        int NumLines = Num_Full_Lines();

            if (NumLines > 0)
                {
                    RemovedRows += NumLines;
                    Points += 100 * ( NumLines * NumLines );
                    emit RowsRemoveChange(GetRemovedRows());
                    emit PointsChange(GetPoints());

                    timer.start( 1000, this);
                    WaitingAfterLine = true;
                    Current_NoShape();
                    update();
                }
    }



void User_Interface::NewBlock()
    {
        InitBoard();
        GetNextBlockType();
        ShowNextBlock();
        Current_X_cordinate = Initial_Piece_position_X();
        Currenty_Y_cordinate = Initial_Piece_position_Y();

        ShowNextBlock();
        if (!MoveTo(CurrentBlock, Current_X_cordinate, Currenty_Y_cordinate))
            {
                CurrentBlock.ChooseShape(No_Shape());
                timer.stop();
                GameStarted = false;
                AutoPlayMode = false;
            }
    }



bool User_Interface::MoveTo(const Pieces &newPiece, int newX, int newY)
    {
        for (int i = 0; i < 4; ++i)
            {
                int x = newX + newPiece.x(i);
                int y = newY - newPiece.y(i);
                if (x < 0 || x >= Board_Width() || y < 0 || y >= Board_Height())
                    return false;
                if (PixelLocation(x, y) != No_Shape())
                    return false;
            }
        CurrentBlock = newPiece;
        Current_X_cordinate = newX;
        Currenty_Y_cordinate = newY;
        update();
        return true;
    }



void User_Interface::timerEvent(QTimerEvent *event)
    {
        if (event->timerId() == timer.timerId())
        {
            if (WaitingAfterLine)
                {
                    WaitingAfterLine = false;
                    NewBlock();
                    timer.start( 1000 , this);
                }
            if(GameStarted)
                {
                    VerticalMovement();
                }
            if(AutoPlayMode)
                Play(this);

        }
        else
            {
                QFrame::timerEvent(event);
            }
    }



